import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [stats, setStats] = useState({ users: 0, rooms: 0, bookings: 0 });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!localStorage.getItem('adminToken')) {
      navigate('/admin/login');
    } else {
      fetchStats();
    }
    // eslint-disable-next-line
  }, [navigate]);

  const fetchStats = async () => {
    setLoading(true);
    setError('');
    try {
      const [usersRes, roomsRes, bookingsRes] = await Promise.all([
        fetch('/api/users', { headers: { Authorization: 'Bearer ' + localStorage.getItem('adminToken') } }),
        fetch('/api/rooms', { headers: { Authorization: 'Bearer ' + localStorage.getItem('adminToken') } }),
        fetch('/api/bookings', { headers: { Authorization: 'Bearer ' + localStorage.getItem('adminToken') } })
      ]);
      if (!usersRes.ok || !roomsRes.ok || !bookingsRes.ok) throw new Error('Failed to fetch stats');
      const users = await usersRes.json();
      const rooms = await roomsRes.json();
      const bookings = await bookingsRes.json();
      setStats({ users: users.length, rooms: rooms.length, bookings: bookings.length });
    } catch (err) {
      setError('Failed to load dashboard stats');
    }
    setLoading(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    navigate('/admin/login');
  };

  return (
    <div className="admin-dashboard-container">
      <h2>Admin Dashboard</h2>
      {loading ? (
        <p>Loading stats...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : (
        <div className="dashboard-stats">
          <div className="dashboard-card">
            <h3>Total Users</h3>
            <p>{stats.users}</p>
            <Link to="/admin/users">Manage Users</Link>
          </div>
          <div className="dashboard-card">
            <h3>Total Rooms</h3>
            <p>{stats.rooms}</p>
            <Link to="/admin/rooms">Manage Rooms</Link>
          </div>
          <div className="dashboard-card">
            <h3>Total Bookings</h3>
            <p>{stats.bookings}</p>
            <Link to="/admin/bookings">Manage Bookings</Link>
          </div>
        </div>
      )}
      <nav>
        <ul>
          <li><Link to="/admin/rooms">Manage Rooms</Link></li>
          <li><Link to="/admin/bookings">Manage Bookings</Link></li>
          <li><Link to="/admin/users">Manage Users</Link></li>
        </ul>
      </nav>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default AdminDashboard;
