import { useEffect, useState } from 'react';
import { FaBars, FaSignInAlt, FaSignOutAlt, FaTimes, FaUser, FaUserPlus } from 'react-icons/fa';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { authUtils } from '../services/api';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [user, setUser] = useState(null);
  const [userType, setUserType] = useState(null);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    const token = authUtils.getToken();
    const savedUserType = localStorage.getItem('userType');
    
    if (token && savedUserType) {
      setUserType(savedUserType);
      if (savedUserType === 'user') {
        const userInfo = localStorage.getItem('userInfo');
        if (userInfo) {
          setUser(JSON.parse(userInfo));
        }
      } else if (savedUserType === 'admin') {
        const adminInfo = localStorage.getItem('adminInfo');
        if (adminInfo) {
          setUser(JSON.parse(adminInfo));
        }
      }
    }
  }, [location]);

  const handleLogout = () => {
    authUtils.removeToken();
    localStorage.removeItem('userInfo');
    localStorage.removeItem('adminInfo');
    localStorage.removeItem('userType');
    setUser(null);
    setUserType(null);
    navigate('/');
    alert('Logged out successfully!');
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const isActive = (path) => location.pathname === path;

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <nav className={`nav nav-left ${isMenuOpen ? 'nav-open' : ''}`}>
            <Link 
              to="/" 
              className={`nav-link ${isActive('/') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Home
            </Link>
            <Link 
              to="/rooms" 
              className={`nav-link ${isActive('/rooms') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Rooms
            </Link>
            <Link 
              to="/deals" 
              className={`nav-link ${isActive('/deals') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Deals
              <span className="hot-badge">Hot</span>
            </Link>
            <Link 
              to="/gallery" 
              className={`nav-link ${isActive('/gallery') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Gallery
            </Link>
          </nav>

          <Link to="/" className="logo">
            <div className="logo-content">
              <h2>Yasin Heaven Star Hotel</h2>
              <img src="/assets/logo.jpg" alt="Yasin Heaven Star Hotel Logo" className="logo-image" />
            </div>
            <p className="logo-subtitle">Premium Hospitality</p>
          </Link>
          
          <nav className={`nav nav-right ${isMenuOpen ? 'nav-open' : ''}`}>
            <Link 
              to="/restaurant" 
              className={`nav-link ${isActive('/restaurant') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Restaurant
            </Link>
            <Link 
              to="/facilities" 
              className={`nav-link ${isActive('/facilities') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Facilities
            </Link>
            <Link 
              to="/booking" 
              className={`nav-link ${isActive('/booking') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              My Bookings
            </Link>
            
            {/* Authentication buttons */}
            {user ? (
              <div className="auth-section" style={{display: 'flex', alignItems: 'center', gap: '10px'}}>
                <span className="user-welcome" style={{color: '#6b7280', fontSize: '0.9rem'}}>
                  Welcome, {user.name || user.email}
                  {userType === 'admin' && <span style={{color: '#dc2626', fontWeight: 'bold'}}> (Admin)</span>}
                </span>
                <button 
                  onClick={handleLogout}
                  className="btn btn-secondary"
                  style={{display: 'flex', alignItems: 'center', gap: '5px'}}
                >
                  <FaSignOutAlt />
                  Logout
                </button>
              </div>
            ) : (
              <div className="auth-buttons" style={{display: 'flex', gap: '10px'}}>
                <Link 
                  to="/login"
                  className="btn btn-outline"
                  onClick={() => setIsMenuOpen(false)}
                  style={{display: 'flex', alignItems: 'center', gap: '5px'}}
                >
                  <FaSignInAlt />
                  Login
                </Link>
                <Link 
                  to="/signup"
                  className="btn btn-primary"
                  onClick={() => setIsMenuOpen(false)}
                  style={{display: 'flex', alignItems: 'center', gap: '5px'}}
                >
                  <FaUserPlus />
                  Sign Up
                </Link>
                <Link 
                  to="/admin/login"
                  className="btn btn-admin"
                  onClick={() => setIsMenuOpen(false)}
                  style={{display: 'flex', alignItems: 'center', gap: '5px', backgroundColor: '#dc2626', color: 'white'}}
                >
                  <FaUser />
                  Admin
                </Link>
              </div>
            )}
          </nav>

          <button className="menu-toggle" onClick={toggleMenu}>
            {isMenuOpen ? <FaTimes /> : <FaBars />}
          </button>

          {/* Mobile Navigation - shows all items */}
          <nav className={`nav nav-mobile ${isMenuOpen ? 'nav-open' : ''}`}>
            <Link 
              to="/" 
              className={`nav-link ${isActive('/') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Home
            </Link>
            <Link 
              to="/rooms" 
              className={`nav-link ${isActive('/rooms') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Rooms
            </Link>
            <Link 
              to="/deals" 
              className={`nav-link ${isActive('/deals') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Deals
              <span className="hot-badge">Hot</span>
            </Link>
            <Link 
              to="/gallery" 
              className={`nav-link ${isActive('/gallery') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Gallery
            </Link>
            <Link 
              to="/restaurant" 
              className={`nav-link ${isActive('/restaurant') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Restaurant
            </Link>
            <Link 
              to="/facilities" 
              className={`nav-link ${isActive('/facilities') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              Facilities
            </Link>
            <Link 
              to="/booking" 
              className={`nav-link ${isActive('/booking') ? 'active' : ''}`}
              onClick={() => setIsMenuOpen(false)}
            >
              My Bookings
            </Link>
            
            {/* Mobile Authentication buttons */}
            {user ? (
              <div className="mobile-auth-section" style={{padding: '10px 0', borderTop: '1px solid #e5e7eb'}}>
                <div style={{color: '#6b7280', fontSize: '0.9rem', marginBottom: '10px'}}>
                  Welcome, {user.name || user.email}
                  {userType === 'admin' && <span style={{color: '#dc2626', fontWeight: 'bold'}}> (Admin)</span>}
                </div>
                <button 
                  onClick={handleLogout}
                  className="btn btn-secondary"
                  style={{width: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '5px'}}
                >
                  <FaSignOutAlt />
                  Logout
                </button>
              </div>
            ) : (
              <div className="mobile-auth-buttons" style={{padding: '10px 0', borderTop: '1px solid #e5e7eb', display: 'flex', flexDirection: 'column', gap: '10px'}}>
                <Link 
                  to="/login"
                  className="btn btn-outline"
                  onClick={() => setIsMenuOpen(false)}
                  style={{display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '5px'}}
                >
                  <FaSignInAlt />
                  Login
                </Link>
                <Link 
                  to="/signup"
                  className="btn btn-primary"
                  onClick={() => setIsMenuOpen(false)}
                  style={{display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '5px'}}
                >
                  <FaUserPlus />
                  Sign Up
                </Link>
                <Link 
                  to="/admin/login"
                  className="btn btn-admin"
                  onClick={() => setIsMenuOpen(false)}
                  style={{display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '5px', backgroundColor: '#dc2626', color: 'white'}}
                >
                  <FaUser />
                  Admin Login
                </Link>
              </div>
            )}
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
