import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaCalendarAlt, FaUsers, FaMapMarkerAlt, FaCheckCircle, FaTimesCircle, FaClock, FaEdit, FaTrash, FaSignInAlt, FaUserPlus } from 'react-icons/fa';
import { authUtils } from '../services/api';
import './BookingPage.css';

const BookingPage = () => {
  const [activeTab, setActiveTab] = useState('upcoming');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    const token = authUtils.getToken();
    const userType = localStorage.getItem('userType');
    
    if (token && userType === 'user') {
      setIsAuthenticated(true);
      const userInfo = localStorage.getItem('userInfo');
      if (userInfo) {
        setUser(JSON.parse(userInfo));
      }
    } else {
      setIsAuthenticated(false);
    }
  }, []);

  // If user is not authenticated, show login prompt
  if (!isAuthenticated) {
    return (
      <div className="booking-page">
        <div className="container">
          <div className="login-prompt" style={{
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            minHeight: '70vh',
            textAlign: 'center',
            backgroundColor: '#f9fafb',
            border: '2px dashed #e5e7eb',
            borderRadius: '12px',
            padding: '40px',
            margin: '20px 0'
          }}>
            <div style={{fontSize: '4rem', color: '#dc2626', marginBottom: '20px'}}>
              🔒
            </div>
            <h1 style={{fontSize: '2.5rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '16px'}}>
              Login Required
            </h1>
            <p style={{fontSize: '1.2rem', color: '#6b7280', marginBottom: '30px', maxWidth: '600px'}}>
              You need to be logged in to view and manage your bookings. 
              Please sign in to access your booking history, upcoming reservations, and manage your account.
            </p>
            
            <div style={{display: 'flex', gap: '20px', flexWrap: 'wrap', justifyContent: 'center'}}>
              <Link 
                to="/login"
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '10px',
                  backgroundColor: '#dc2626',
                  color: 'white',
                  padding: '12px 30px',
                  borderRadius: '8px',
                  textDecoration: 'none',
                  fontSize: '1.1rem',
                  fontWeight: '600',
                  transition: 'all 0.3s ease',
                  boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
                }}
                onMouseOver={(e) => e.target.style.backgroundColor = '#b91c1c'}
                onMouseOut={(e) => e.target.style.backgroundColor = '#dc2626'}
              >
                <FaSignInAlt />
                Sign In
              </Link>
              
              <Link 
                to="/signup"
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '10px',
                  backgroundColor: 'transparent',
                  color: '#dc2626',
                  border: '2px solid #dc2626',
                  padding: '12px 30px',
                  borderRadius: '8px',
                  textDecoration: 'none',
                  fontSize: '1.1rem',
                  fontWeight: '600',
                  transition: 'all 0.3s ease'
                }}
                onMouseOver={(e) => {
                  e.target.style.backgroundColor = '#dc2626';
                  e.target.style.color = 'white';
                }}
                onMouseOut={(e) => {
                  e.target.style.backgroundColor = 'transparent';
                  e.target.style.color = '#dc2626';
                }}
              >
                <FaUserPlus />
                Create Account
              </Link>
            </div>
            
            <div style={{
              marginTop: '40px',
              padding: '20px',
              backgroundColor: '#fef3c7',
              border: '1px solid #f59e0b',
              borderRadius: '8px',
              maxWidth: '500px'
            }}>
              <h3 style={{color: '#92400e', marginBottom: '10px', fontSize: '1.1rem'}}>
                Why create an account?
              </h3>
              <ul style={{color: '#92400e', textAlign: 'left', margin: 0, paddingLeft: '20px'}}>
                <li>View your booking history</li>
                <li>Manage upcoming reservations</li>
                <li>Access exclusive member deals</li>
                <li>Faster checkout process</li>
                <li>24/7 customer support</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }

  const bookings = [
    {
      id: 1,
      hotel: "Luxury Beach Resort",
      location: "Miami Beach, FL",
      checkIn: "2025-08-15",
      checkOut: "2025-08-20",
      guests: 2,
      rooms: 1,
      status: "confirmed",
      totalPrice: 1495,
      image: "/assets/Home 1.jpg",
      bookingDate: "2025-06-20",
      confirmationCode: "YH12345"
    },
    {
      id: 2,
      hotel: "Downtown Business Hotel",
      location: "Manhattan, NY",
      checkIn: "2025-09-10",
      checkOut: "2025-09-12",
      guests: 1,
      rooms: 1,
      status: "pending",
      totalPrice: 398,
      image: "/assets/Master .jpg",
      bookingDate: "2025-06-22",
      confirmationCode: "YH12346"
    },
    {
      id: 3,
      hotel: "Mountain View Lodge",
      location: "Aspen, CO",
      checkIn: "2025-05-10",
      checkOut: "2025-05-15",
      guests: 4,
      rooms: 2,
      status: "completed",
      totalPrice: 1745,
      image: "/assets/View.jpg",
      bookingDate: "2025-03-15",
      confirmationCode: "YH12347"
    },
    {
      id: 4,
      hotel: "Historic Grand Hotel",
      location: "Savannah, GA",
      checkIn: "2025-07-01",
      checkOut: "2025-07-05",
      guests: 2,
      rooms: 1,
      status: "cancelled",
      totalPrice: 916,
      image: "/assets/Home 2.jpg",
      bookingDate: "2025-04-20",
      confirmationCode: "YH12348"
    }
  ];

  const getStatusIcon = (status) => {
    switch (status) {
      case 'confirmed':
        return <FaCheckCircle className="status-icon confirmed" />;
      case 'pending':
        return <FaClock className="status-icon pending" />;
      case 'completed':
        return <FaCheckCircle className="status-icon completed" />;
      case 'cancelled':
        return <FaTimesCircle className="status-icon cancelled" />;
      default:
        return null;
    }
  };

  const getStatusClass = (status) => {
    return `status-badge ${status}`;
  };

  const filterBookings = (status) => {
    if (status === 'upcoming') {
      return bookings.filter(b => b.status === 'confirmed' || b.status === 'pending');
    } else if (status === 'past') {
      return bookings.filter(b => b.status === 'completed' || b.status === 'cancelled');
    }
    return bookings;
  };

  const filteredBookings = filterBookings(activeTab);

  const handleCancelBooking = (bookingId) => {
    // Handle booking cancellation
    console.log('Cancelling booking:', bookingId);
  };

  const handleModifyBooking = (bookingId) => {
    // Handle booking modification
    console.log('Modifying booking:', bookingId);
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      weekday: 'short',
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  const calculateNights = (checkIn, checkOut) => {
    const start = new Date(checkIn);
    const end = new Date(checkOut);
    const diffTime = Math.abs(end - start);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays;
  };

  return (
    <div className="booking-page">
      <div className="booking-header">
        <div className="container">
          <h1>My Bookings</h1>
          <p>Manage your hotel reservations and travel plans</p>
        </div>
      </div>

      <div className="booking-content">
        <div className="container">
          <div className="booking-tabs">
            <button
              className={`tab-btn ${activeTab === 'upcoming' ? 'active' : ''}`}
              onClick={() => setActiveTab('upcoming')}
            >
              Upcoming Bookings
            </button>
            <button
              className={`tab-btn ${activeTab === 'past' ? 'active' : ''}`}
              onClick={() => setActiveTab('past')}
            >
              Past Bookings
            </button>
            <button
              className={`tab-btn ${activeTab === 'all' ? 'active' : ''}`}
              onClick={() => setActiveTab('all')}
            >
              All Bookings
            </button>
          </div>

          <div className="bookings-list">
            {filteredBookings.length === 0 ? (
              <div className="no-bookings">
                <h3>No bookings found</h3>
                <p>You don't have any bookings in this category.</p>
                <button className="btn btn-primary">Book Now</button>
              </div>
            ) : (
              filteredBookings.map(booking => (
                <div key={booking.id} className="booking-card">
                  <div className="booking-image">
                    <img src={booking.image} alt={booking.hotel} />
                  </div>
                  
                  <div className="booking-info">
                    <div className="booking-header-info">
                      <h3>{booking.hotel}</h3>
                      <div className={getStatusClass(booking.status)}>
                        {getStatusIcon(booking.status)}
                        {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                      </div>
                    </div>
                    
                    <div className="booking-location">
                      <FaMapMarkerAlt />
                      {booking.location}
                    </div>
                    
                    <div className="booking-details">
                      <div className="detail-item">
                        <FaCalendarAlt />
                        <span>Check-in: {formatDate(booking.checkIn)}</span>
                      </div>
                      <div className="detail-item">
                        <FaCalendarAlt />
                        <span>Check-out: {formatDate(booking.checkOut)}</span>
                      </div>
                      <div className="detail-item">
                        <FaUsers />
                        <span>{booking.guests} guests, {booking.rooms} room{booking.rooms > 1 ? 's' : ''}</span>
                      </div>
                      <div className="detail-item">
                        <FaClock />
                        <span>{calculateNights(booking.checkIn, booking.checkOut)} nights</span>
                      </div>
                    </div>
                    
                    <div className="booking-confirmation">
                      <strong>Confirmation Code: {booking.confirmationCode}</strong>
                    </div>
                  </div>
                  
                  <div className="booking-actions">
                    <div className="booking-price">
                      <span className="price-label">Total Price</span>
                      <span className="price-value">${booking.totalPrice}</span>
                    </div>
                    
                    <div className="action-buttons">
                      {booking.status === 'confirmed' && (
                        <>
                          <button 
                            className="btn btn-secondary"
                            onClick={() => handleModifyBooking(booking.id)}
                          >
                            <FaEdit />
                            Modify
                          </button>
                          <button 
                            className="btn btn-danger"
                            onClick={() => handleCancelBooking(booking.id)}
                          >
                            <FaTrash />
                            Cancel
                          </button>
                        </>
                      )}
                      
                      {booking.status === 'pending' && (
                        <button 
                          className="btn btn-danger"
                          onClick={() => handleCancelBooking(booking.id)}
                        >
                          <FaTrash />
                          Cancel
                        </button>
                      )}
                      
                      {booking.status === 'completed' && (
                        <button className="btn btn-primary">
                          Book Again
                        </button>
                      )}
                      
                      <button className="btn btn-outline">
                        View Details
                      </button>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookingPage;
