import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { adminAPI, authUtils } from '../services/api';

const AdminLogin = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    
    try {
      const response = await adminAPI.login({ email, password });
      
      if (response.data.token) {
        // Save token and admin info
        authUtils.saveToken(response.data.token);
        localStorage.setItem('adminInfo', JSON.stringify(response.data.admin));
        
        console.log('‚úÖ Admin login successful!');
        console.log('üìã Admin info:', response.data.admin);
        
        // Show success message
        alert('Login successful! Redirecting to admin dashboard...');
        
        // Call onLogin callback if provided
        if (onLogin) onLogin(response.data.admin);
        
        // Redirect to admin dashboard
        navigate('/admin');
        
      } else {
        setError('Login failed - no token received');
      }
    } catch (err) {
      console.error('‚ùå Login error:', err);
      setError(err.response?.data?.message || 'Login failed. Please check your credentials.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="admin-login-container" style={{
      maxWidth: '400px',
      margin: '50px auto',
      padding: '2rem',
      border: '1px solid #ddd',
      borderRadius: '8px',
      backgroundColor: '#f9f9f9'
    }}>
      <h2 style={{textAlign: 'center', marginBottom: '1rem'}}>Admin Login</h2>
      
      <div className="login-info" style={{
        backgroundColor: '#e7f3ff',
        padding: '1rem',
        borderRadius: '4px',
        marginBottom: '1rem',
        fontSize: '0.9rem'
      }}>
        <p><strong>Default Admin Credentials:</strong></p>
        <p>Email: admin@yasinheavenstar.com</p>
        <p>Password: admin123</p>
      </div>
      
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Admin Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
          disabled={loading}
          style={{
            width: '100%',
            padding: '10px',
            margin: '10px 0',
            border: '1px solid #ddd',
            borderRadius: '4px',
            fontSize: '16px'
          }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
          disabled={loading}
          style={{
            width: '100%',
            padding: '10px',
            margin: '10px 0',
            border: '1px solid #ddd',
            borderRadius: '4px',
            fontSize: '16px'
          }}
        />
        <button 
          type="submit" 
          disabled={loading}
          style={{
            width: '100%',
            padding: '12px',
            margin: '10px 0',
            backgroundColor: loading ? '#ccc' : '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            fontSize: '16px',
            cursor: loading ? 'not-allowed' : 'pointer'
          }}
        >
          {loading ? 'Logging in...' : 'Login'}
        </button>
        {error && (
          <div className="error" style={{
            color: 'red',
            backgroundColor: '#ffe6e6',
            padding: '10px',
            borderRadius: '4px',
            marginTop: '10px'
          }}>
            {error}
          </div>
        )}
      </form>
    </div>
  );
};

export default AdminLogin;
